BEGIN
  EXEC tSQLt.Run @TestName = @TestName, @TestResultFormatter = 'tSQLt.NullTestResultFormatter';
END;

CREATE PROCEDURE [tSQLt].[RunWithNullResults]
    @TestName NVARCHAR(MAX) = NULL
AS

BEGIN
  EXEC tSQLt.Run @TestName = @TestName, @TestResultFormatter = 'tSQLt.XmlResultFormatter';
END;

CREATE PROCEDURE [tSQLt].[RunWithXmlResults]
   @TestName NVARCHAR(MAX) = NULL
AS

BEGIN
  DECLARE @ViewName NVARCHAR(MAX);

  DECLARE viewNames CURSOR LOCAL FAST_FORWARD FOR
   SELECT QUOTENAME(OBJECT_SCHEMA_NAME(t.parent_id)) + '.' + QUOTENAME(OBJECT_NAME(t.parent_id)) AS viewName
     FROM sys.extended_properties ep
     JOIN sys.triggers t
       on ep.major_id = t.object_id
     WHERE ep.name = N'SetFakeViewOnTrigger'  
  OPEN viewNames;

  FETCH NEXT FROM viewNames INTO @ViewName;
  WHILE @@FETCH_STATUS = 0
  BEGIN
    EXEC tSQLt.Private_SetFakeViewOff_SingleView @ViewName;

    FETCH NEXT FROM viewNames INTO @ViewName;
  END;

  CLOSE viewNames;
  DEALLOCATE viewNames;
END;

CREATE PROCEDURE [tSQLt].[SetFakeViewOff]
  @SchemaName NVARCHAR(MAX)
AS

BEGIN
  DECLARE @ViewName NVARCHAR(MAX);

  DECLARE viewNames CURSOR LOCAL FAST_FORWARD FOR
  SELECT QUOTENAME(OBJECT_SCHEMA_NAME(object_id)) + '.' + QUOTENAME([name]) AS viewName
    FROM sys.views
   WHERE schema_id = SCHEMA_ID(@SchemaName);

  OPEN viewNames;

  FETCH NEXT FROM viewNames INTO @ViewName;
  WHILE @@FETCH_STATUS = 0
  BEGIN
    EXEC tSQLt.Private_SetFakeViewOn_SingleView @ViewName;

    FETCH NEXT FROM viewNames INTO @ViewName;
  END;

  CLOSE viewNames;
  DEALLOCATE viewNames;
END;

CREATE PROCEDURE [tSQLt].[SetFakeViewOn]
  @SchemaName NVARCHAR(MAX)
AS

BEGIN
    IF EXISTS (SELECT 1 FROM sys.extended_properties WHERE [name] = N'tSQLt.ResultsFormatter')
    BEGIN
        EXEC sp_dropextendedproperty @name = N'tSQLt.ResultsFormatter',
                                    @level0type = 'SCHEMA',
                                    @level0name = 'tSQLt',
                                    @level1type = 'PROCEDURE',
                                    @level1name = 'Private_OutputTestResults';
    END;

    EXEC sp_addextendedproperty @name = N'tSQLt.ResultsFormatter', 
                                @value = @Formatter,
                                @level0type = 'SCHEMA',
                                @level0name = 'tSQLt',
                                @level1type = 'PROCEDURE',
                                @level1name = 'Private_OutputTestResults';
END;

CREATE PROCEDURE [tSQLt].[SetTestResultFormatter]
    @Formatter NVARCHAR(4000)
AS

BEGIN
  EXEC tSQLt.Private_SetConfiguration @Name = 'Verbose', @Value = @Verbose;
END;

CREATE PROCEDURE [tSQLt].[SetVerbose]
  @Verbose BIT = 1
AS

BEGIN
    DECLARE @ProcedureObjectId INT;
    SELECT @ProcedureObjectId = OBJECT_ID(@ProcedureName);

    EXEC tSQLt.Private_ValidateProcedureCanBeUsedWithSpyProcedure @ProcedureName;

    DECLARE @LogTableName NVARCHAR(MAX);
    SELECT @LogTableName = QUOTENAME(OBJECT_SCHEMA_NAME(@ProcedureObjectId)) + '.' + QUOTENAME(OBJECT_NAME(@ProcedureObjectId)+'_SpyProcedureLog');

    EXEC tSQLt.Private_RenameObjectToUniqueNameUsingObjectId @ProcedureObjectId;

    EXEC tSQLt.Private_CreateProcedureSpy @ProcedureObjectId, @ProcedureName, @LogTableName, @CommandToExecute;

    RETURN 0;
END;

CREATE PROCEDURE [tSQLt].[SpyProcedure]
    @ProcedureName NVARCHAR(MAX),
    @CommandToExecute NVARCHAR(MAX) = NULL
AS

BEGIN
    SET @txt = tSQLt.Private::TableToString(@TableName, @OrderBy, @PrintOnlyColumnNameAliasList);
END;

CREATE PROCEDURE [tSQLt].[TableToText]
    @txt NVARCHAR(MAX) OUTPUT,
    @TableName NVARCHAR(MAX),
    @OrderBy NVARCHAR(MAX) = NULL,
    @PrintOnlyColumnNameAliasList NVARCHAR(MAX) = NULL
AS

BEGIN
  DROP TYPE tSQLt.Private;

  EXEC tSQLt.DropClass 'tSQLt';  

  DROP ASSEMBLY tSQLtCLR;
END;

BEGIN DECLARE @XmlOutput XML;
SELECT @XmlOutput = ( SELECT *--Tag, Parent, [testsuites!1!hide!hide], [testsuite!2!name], [testsuite!2!tests], [testsuite!2!errors], [testsuite!2!failures], [testsuite!2!timestamp], [testsuite!2!time], [testcase!3!classname], [testcase!3!name], [testcase!3!time], [failure!4!message] FROM ( SELECT 1 AS Tag, NULL AS Parent, 'root' AS [testsuites!1!hide!hide], NULL AS [testsuite!2!id], NULL AS [testsuite!2!name], NULL AS [testsuite!2!tests], NULL AS [testsuite!2!errors], NULL AS [testsuite!2!failures], NULL AS [testsuite!2!timestamp], NULL AS [testsuite!2!time], NULL AS [testsuite!2!hostname], NULL AS [testsuite!2!package], NULL AS [properties!3!hide!hide], NULL AS [testcase!4!classname], NULL AS [testcase!4!name], NULL AS [testcase!4!time], NULL AS [failure!5!message], NULL AS [failure!5!type], NULL AS [error!6!message], NULL AS [error!6!type], NULL AS [system-out!7!hide], NULL AS [system-err!8!hide] UNION ALL SELECT 2 AS Tag, 1 AS Parent, 'root', ROW_NUMBER()OVER(ORDER BY Class), Class, COUNT(1), SUM(CASE Result WHEN 'Error' THEN 1 ELSE 0 END), SUM(CASE Result WHEN 'Failure' THEN 1 ELSE 0 END), CONVERT(VARCHAR(19),MIN(TestResult.TestStartTime),126), CAST(CAST(DATEDIFF(MILLISECOND,MIN(TestResult.TestStartTime),MAX(TestResult.TestEndTime))/1000.0 AS NUMERIC(20,3))AS VARCHAR(MAX)), CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(MAX)), 'tSQLt', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL FROM tSQLt.TestResult GROUP BY Class UNION ALL SELECT 3 AS Tag, 2 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL FROM tSQLt.TestResult GROUP BY Class UNION ALL SELECT 4 AS Tag, 2 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, TestCase, CAST(CAST(DATEDIFF(MILLISECOND,TestResult.TestStartTime,TestResult.TestEndTime)/1000.0 AS NUMERIC(20,3))AS VARCHAR(MAX)), NULL, NULL, NULL, NULL, NULL, NULL FROM tSQLt.TestResult UNION ALL SELECT 5 AS Tag, 4 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, TestCase, CAST(CAST(DATEDIFF(MILLISECOND,TestResult.TestStartTime,TestResult.TestEndTime)/1000.0 AS NUMERIC(20,3))AS VARCHAR(MAX)), Msg, 'tSQLt.Fail', NULL, NULL, NULL, NULL FROM tSQLt.TestResult WHERE Result IN ('Failure') UNION ALL SELECT 6 AS Tag, 4 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, TestCase, CAST(CAST(DATEDIFF(MILLISECOND,TestResult.TestStartTime,TestResult.TestEndTime)/1000.0 AS NUMERIC(20,3))AS VARCHAR(MAX)), NULL, NULL, Msg, 'SQL Error', NULL, NULL FROM tSQLt.TestResult WHERE Result IN ( 'Error') UNION ALL SELECT 7 AS Tag, 2 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL FROM tSQLt.TestResult GROUP BY Class UNION ALL SELECT 8 AS Tag, 2 AS Parent, 'root', NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, Class, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL FROM tSQLt.TestResult GROUP BY Class ) AS X ORDER BY [testsuite!2!name],CASE WHEN Tag IN (7,8) THEN 1 ELSE 0 END, [testcase!4!name], Tag FOR XML EXPLICIT );
EXEC tSQLt.Private_PrintXML @XmlOutput; END;
